-- ----------------------------
-- Table structure for newsinfo
-- ----------------------------
DROP TABLE IF EXISTS `newsinfo`;
CREATE TABLE `newsinfo`  (
  `Id` int(4) NOT NULL AUTO_INCREMENT COMMENT '新闻编号，主键，自增',
  `Title` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '新闻标题名',
  `Author` varchar(10) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '新闻发布人',
  `CreateDate` datetime NULL DEFAULT NULL COMMENT '发布时间',
  `Content` varchar(10000) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '新闻内容',
  `Summary` varchar(500) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL COMMENT '新闻摘要',
  `Tid` int(4) NULL DEFAULT NULL COMMENT '所属主题，外键',
  PRIMARY KEY (`Id`) USING BTREE,
  INDEX `Tid`(`Tid`) USING BTREE,
  CONSTRAINT `newsinfo_ibfk_1` FOREIGN KEY (`Tid`) REFERENCES `topic` (`Id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Compact;

